Array.prototype.indexOf = function(val) {
    for (var i = 0; i < this.length; i++) {
        if (this[i] == val) return i;
    }
    return -1;
};
Array.prototype.remove = function(val) {
    var index = this.indexOf(val);
    if (index > -1) {
        this.splice(index, 1);
    }
};
/* globals jQuery, $ */
var hide_count,
    $ = unsafeWindow.$ || $;
(function (window, undefined) {
    var Mytoast = function () { //toast匿名构造函数, 用来进行低干扰性提示
    };
    Mytoast.prototype = {
        create: function (str, duration) {
            let self = this;
            let toastHtml = '';
            let toastText = '<div class="my-toast-text" style="z-index:1024">' + str + '</div>';
            toastHtml = '<div class="my-toast" style="z-index:1024">' + toastText + '</div>';
            if ($(".my-toast").length > 0) {
                $(".my-toast").remove();
                if (typeof(hide_count) !== "undefined") {
                    clearTimeout(hide_count);
                }
            }
            document.body.insertAdjacentHTML('beforeend', toastHtml);
            duration == null ? duration = 2e3 : '';
            self.show();
            hide_count = setTimeout(() => {
                self.hide();
            }, duration);
        },
        show: function () {
            let self = this;
            document.querySelector(".my-toast").style.display = "block";
            document.querySelector(".my-toast").style.marginTop = "-" + Math.round(document.querySelector(".my-toast").offsetHeight / 2) + "px";
            if (document.querySelector(".my-toast")) return 0;
        },
        hide: function () {
            let self = this;
            if (document.querySelector(".my-toast")) {
                document.querySelector(".my-toast").parentNode.removeChild(document.querySelector(".my-toast"));
            }
        },
        toast: function (str, duration) {
            let self = this;
            console.info("%c[pigai toolkit]" + str, "color:black;background-color:lightskyblue;");
            return self.create(str, duration);
        }
    };
    window.Mytoast = Mytoast;
}(window));

//破解相关函数闭包
const crack = {
    //禁用alert
    alert_disable: () => {
        unsafeWindow.window.alert = function () {
            return false;
        };
    },

    timer_disable: () => {
        unsafeWindow.msg("检测到您的老师设置了时间限制, 已为您创建禁用强制提交按钮, 误判请反馈, 反馈时请带上网址");
        $('#title').after("<button id='timer'>禁用强制提交</button>").after("<button id='TitleFill'>填充标题</button>");
        $('#timer').click
        (
            () => {
                $('.btn.btn-primary').attr('id', 'timer_disabled');
                toolkit.simpleToast.toast("时间限制已解除, 到时提示请无视, 误判请反馈, 反馈时请带上网址", 3e3);
                $('#timer').html("时限解除").attr("disabled", true).css("color", "grey");
                setTimeout(() => {
                    $('#timer').remove();
                }, 5e3);
                crack.alert_disable();
            });
        $('#TitleFill').click
        (
            () => {
                toolkit.title_fill();
            }
        );
    },

    timer_disable_2: () => {
        unsafeWindow.msg("检测到您的老师设置了时间限制, 已为您创建禁用强制提交按钮, 误判请反馈, 反馈时请带上网址");
        $('#title').after("<button id='timer'>禁用强制提交</button>");
        $('#timer').click
        (
            () => {
                $('.btn.btn-primary').attr('id', 'timer_disabled');
                toolkit.simpleToast.toast("时间限制已解除, 到时提示请无视, 误判请反馈, 反馈时请带上网址", 3e3);
                $('#timer').html("时限解除").attr("disabled", true).css("color", "grey");
                setTimeout(() => {
                    $('#timer').remove();
                }, 5e3);
                crack.alert_disable();
            });
    },

    main: (pointer) => {
        $('#contents').unbind();
        toolkit.simpleToast.toast("文本框解绑, 现在可以粘贴", 3e3);
        $('.tips2 ul').html("<li><span style='font-size: x-small;'>您的老师启用了'禁止粘贴'选项, 但是这是徒劳的。Enjoy pasting!</span></li><li><span style='font-size: x-small;'>批改网有查重机制, 请不要从同一处复制太多.</span></li><li><span style='font-size: x-small;'><del>相似作文的显示有延迟, 一般等上15分钟还不显示相似就说明稳了</del> 现在相似详情可直接在打分界面查看(脚本功能)</li></span>");
        //$(".tips2").after("<br><br>");
        if ($("#title").length > 0) {
            $('#title').after("<button id='spss'>字数统计</button>").after("<button id='TitleFill'>填充标题</button>");
            if (pointer === 1) {
                crack.timer_disable_2();
            }
            $('#spss').click
            (
                () => {
                    toolkit.len = toolkit.spss_countwords($('#contents').val());
                    $('#init_wc').html(toolkit.len);
                }
            );
            $('#TitleFill').click
            (
                () => {
                    toolkit.title_fill();
                }
            );
        }
    },

    kind: () => {
        $('#title').after("<button id='spss'>字数统计</button>").after("<button id='TitleFill'>填充标题</button>");
        $('#TitleFill').click
        (
            () => {
                toolkit.title_fill();
            }
        );
        $('#spss').click
        (
            () => {
                toolkit.len = toolkit.spss_countwords($('#contents').val());
                $('#init_wc').html(toolkit.len);
            }
        );
    }
};

//工具相关函数闭包
const toolkit = {
    len: 0,
    /* eslint-disable-next-line */
    simpleToast: new Mytoast(),
    choice: -1,
    old: false,

    my_tofixed: (value, n, no_string = 0) => {
        let f = Math.round(value * Math.pow(10, n)) / Math.pow(10, n);
        if (no_string) {
            return f;
        }
        let s = f.toString();
        let rs = s.indexOf('.');
        if (rs < 0) {
            s += '.';
        }
        for (let i = s.length - s.indexOf('.'); i <= n; i++) {
            s += "0";
        }
        return s;
    },

    title_fill: () => {
        let title = $(".title__text.flex").text(),
            a = title.indexOf("："),
            b = title.indexOf("答");
        title = title.substring(a + 1, b).replace(/(\s*$)/g, "").replace(/(^\s*)|(\s*$)/g, "");
        document.getElementsByTagName("title")[0].innerText = title;
        $("#title").val(title);
        toolkit.simpleToast.toast("[自动填充标题] 成功", 3e3);
    },

    find: (str, cha, num) => {
        let x = str.indexOf(cha);
        for (let i = 0; i < num; i++) {
            x = str.indexOf(cha, x + 1);
        }
        return x;
    },

    donate: () => {
        $("body").append(`<div class='donate-me' title='感谢老板~'><span class='donate-inner'>请我喝杯咖啡<span style="padding-left:16px">☕</span></span></div><div class='QRCode'></div>`);
        $("[class='donate-me']").on(`mouseover mouseout`, () => {
            $("[class='QRCode']").css("display", $(".QRCode").is(":hidden") ? "block" : "none");
        });
    },

    spss_countwords: (str) => {
        let content = String();
        content = $.trim(str);
        if (content === '') {
            return 0;
        }
        else {
            let arr = [];
            arr = content.match(/([a-zA-Z'\d]+[a-zA-Z'\d-]*[a-zA-Z'\d]+|[a-zA-Z'\d]+)/g);
            if (arr === null) {
                return 0;
            }
            else {
                return arr.length;
            }
        }
    },

    getText: (par) => {
        let str_get = "";
        par.find('span').each(function () {
            str_get += $(this).text().replace(/&nbsp;/, ''); //.replace(/%C2A0+/g," ")
        });
        return str_get === "" ? $('.direction.flexbox .flex div p').text() : $.trim(str_get);
    },

    getRid: (pos) => {
        let rid = "",
            text = window.location.href;
        while (!isNaN(parseInt(text.charAt(pos)))) {
            rid += text.charAt(pos);
            pos++;
        }
        return parseInt(rid)
    },

    add_link: () => {
        //增加超链接
        let s2 = toolkit.getText($('.direction.flexbox .flex div p'));
        s2 = $.trim(s2);
        GM_addStyle(`.trans span{display:none}`);
        let se_bai_0 = "https://www.baidu.com/s?wd=",
            se_google_0 = "https://www.google.com/search?q=",
            se_bai = se_bai_0 + s2,
            se_google = se_google_0 + s2,
            text =
            `<form id="links" ondragstart="return false;">
   <fieldset>
    <legend>常用外链<sup><a id="link-set">编辑</a></sup></legend>
    <div class="trans">
     <span id="link-1"><img src="https://www.baidu.com/favicon.ico" /><a href="${decodeURIComponent(se_bai)}" target="_blank">百度直接搜索</a></span>
     <span id="link-2"><img src="https://www.google.cn/favicon.ico" /><a href="${decodeURIComponent(se_google)}" target="_blank" title="需要翻墙">谷歌直接搜索</a></span>
     <span id="link-3"><img src="https://fanyi.baidu.com/favicon.ico" width="1" /><a href="https://fanyi.baidu.com" target="_blank">百度翻译</a></span>
     <span id="link-4"><img src="https://translate.google.cn/favicon.ico" /><a href="https://translate.google.cn" target="_blank" title="不需要翻墙">谷歌翻译</a></span>
     <span id="link-5"><img src="http://shared.ydstatic.com/images/favicon.ico" /><a href="http://fanyi.youdao.com/" target="_blank">有道翻译</a></span>
     <span id="link-6"><img src="data:image/png;base64," /><a href="http://www.iciba.com/" target="_blank">金山词霸</a></span>
     <span id="link-7"><img src="data:image/png;base64," /><a class="fail" title="脚本破解粘贴失效时可能会有用">手动允许粘贴</a></span>
    </div>
   </fieldset>
  </form>`;
        $(".direction.flexbox").after(text);
        let t;
        try
        {
            t=JSON.parse(GM_getValue("links"));
        }
        catch(e)
        {
            t=[];
        }
        for(let p=0 ; p<t.length ; p++)
        {
            $(`#link-${t[p]}`).show();
        }
        //定位
        if(t.length===0)
        {
            GM_addStyle(`.trans span{display:inline}`);
            GM_setValue("links",JSON.stringify([1,2,3,4,5,6,7]));
        }
        $(".trans span").append(`<sup class="delete-link" style="display:none"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="15" height="15"><path fill="none" d="M0 0h24v24H0z"/><path d="M12 22C6.477 22 2 17.523 2 12S6.477 2 12 2s10 4.477 10 10-4.477 10-10 10zm0-2a8 8 0 1 0 0-16 8 8 0 0 0 0 16zm0-9.414l2.828-2.829 1.415 1.415L13.414 12l2.829 2.828-1.415 1.415L12 13.414l-2.828 2.829-1.415-1.415L10.586 12 7.757 9.172l1.415-1.415L12 10.586z"/></svg></sup>`);
        $(".delete-link").click(function(){
            let id=$(this).parent().attr("id");
            $(`#${id}`).remove();
            id=parseInt(id.match(/[0-9]/g)[0]);
            t.remove(id);
            GM_setValue("links",JSON.stringify(t));
            toolkit.simpleToast.toast("设置已保存");
        });
        $("#link-set").click(function(){
            try
            {
                $(".delete-link").css("display", $(".delete-link").attr("style").match("none")!==null ? "inline-block" : "none");
            }
            catch(e)
            {
                toolkit.simpleToast.toast("外链全部删除会触发异常, 请恢复初始后重新配置",5e3);
                return -1;
            }
            $(this).html($(this).text().indexOf("编辑") > -1 ? "完成 <a id='roolback'>恢复初始</a>" : "编辑");
            $("#roolback").click(()=>{
                $("#links").remove();
                GM_setValue("links",JSON.stringify([1,2,3,4,5,6,7]));
                toolkit.add_link();
                $("#links").css("transform", "none");
            })
        })
        $(".fail").click((e) => {
            e.preventDefault();
            e.stopPropagation();
            $("#contents").unbind();
            $("#contents").focus(function () {
                $(this).css("background-color", "#ffffff").css("color","black");
            });
            $("#contents").blur(function () {
                $(this).css("background-color", "#f9f9f9").css("color","#909090");
            });
            $('#contents').on('input propertychange', function () {
                toolkit.len = toolkit.spss_countwords($(this).val());
                $('#init_wc').html(toolkit.len);
                if (toolkit.len === 0) {
                    $('#spss').attr("disabled", true).css("color", "grey");
                }
                else {
                    $('#spss').attr("disabled", false).css("color", "blue");
                }
            });
            toolkit.simpleToast.toast(`手动破解粘贴可能已完成,基于作文框的某些功能可能会失效`,5e3);
        })
    },

    //非阻塞型confirm
    dialogBox: (message, yesCallback, noCallback) => {
        if (message) {
            $("body").append("<div class='wrap-dialog hide'>\<div class='dialog'>\<div class='dialog-header'>\<span class='dialog-title'>试图跳转确认</span></div><div class='dialog-body'><span class='dialog-message'></span></div><div class='dialog-footer'><input type='button' class='btn_my' id='confirm' value='确认' /><input type='button' class='btn_my ml50' id='cancel' value='取消' /></div></div></div>");
            $('span[class="dialog-message"]').html(message);
        }
        $('.wrap-dialog').removeClass("hide");
        $('#confirm').focus();
        $('#confirm').click(function () {
            $('.wrap-dialog').addClass("hide");
            yesCallback();
        });
        $('#cancel').click(function () {
            $('.wrap-dialog').addClass("hide");
            noCallback();
        });
    },

    //旧版界面跳转
    old_jump: () => {
        if ($(".tbMenu2010").length > 0 && GM_getValue("old") !== 100) {
            toolkit.old=true;
            $("body").append("<div class='dialog-message'></div>");
            let message = `\u68c0\u6d4b\u5230\u60a8\u5f53\u524d\u5904\u4e8e\u65e7\u7248\u6279\u6539\u7f51\u754c\u9762, \u8fd9\u4f1a\u4f7f\u5f97\u811a\u672c\u5de5\u4f5c\u5f02\u5e38, \u662f\u5426\u5c1d\u8bd5\u8df3\u8f6c\u5230\u65b0\u7248\u6279\u6539\u7f51\u754c\u9762?<span style='color:red'>\u26a0\u6ce8\u610f: \u672c\u6d88\u606f\u53d6\u6d88\u540e\u5c06\u4e0d\u4f1a\u518d\u6b21\u5f39\u51fa\u4f46\u662f\u53ef\u4ee5\u5728\u6cb9\u7334\u83dc\u5355\u4e2d\u518d\u6b21\u89e6\u53d1</span>`;
            toolkit.dialogBox(message, () => {
                document.cookie = "old=2017;path=/";
                document.cookie = "old=2017;path=/;domain=pigai.org";
                GM_setValue("old", 1);
                location.reload();
            },
                              () => {
                GM_setValue("old", 100);
                location.reload()
            });
        }
    },

    init: async () => {
        GM_registerMenuCommand("提个issue",() => {
            window.open(`https://github.com/consideration-ak/pigaiwang-toolkit/issues`);
        });
        GM_registerMenuCommand("使用说明",() => {
            window.open(`https://github.com/consideration-ak/pigaiwang-toolkit/tree/master`);
        });
        GM_registerMenuCommand("强制跳转到新版",() => {
            document.cookie = "old=2017;path=/";
            document.cookie = "old=2017;path=/;domain=pigai.org";
            GM_setValue("old", 1);
            location.reload();
        });
        if ($("#redirect_msg").text().match("登录") !== null) {
            toolkit.simpleToast.toast("未登录, 脚本停止运行");
            return 2;
        }
        toolkit.old_jump();
        if(toolkit.old)
        {
            return 0;
        }
        if(GM_getValue("upd")!==100)
        {
            $("body").append("<div class='dialog-message'></div>");
            GM_addStyle(`.dialog-body{height:70px !important}.dialog-header{height:30px !important}`)
            let msg = `\u5982\u679c\u60a8\u4e0d\u662f\u4ecegreasy fork\u6216github\u5b89\u88c5\u7684\u672c\u811a\u672c, \u8bf7\u70b9\u51fb\u786e\u8ba4\u91cd\u65b0\u5b89\u88c5\u672c\u811a\u672c\u4ee5\u83b7\u53d6\u66f4\u65b0\u63a8\u9001, \u5426\u5219\u8bf7\u5ffd\u7565`
            toolkit.dialogBox(msg, () => {
                window.open(`https://greasyfork.org/zh-CN/scripts/393386`);
                GM_setValue("upd", 100);
            },
                              () => {
                GM_setValue("upd", 100);
            });
        }
        toolkit.donate();
        if (window.location.href.match("view") !== null) {
            //当前是评分界面, 增加相似详情
            $(".panel").append(`<br><a class='copy_hidden'>查看作文相似性对比(脚本功能)</a><br><br>选择版本: <select id='version-choice' style="width:6.5em"></select><span title="选择你要比对相似度的版本号, 感谢RoyMikeJiang提出的issue" style="cursor:pointer"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="15"><path fill="none" d="M0 0h24v24H0z"></path><path d="M12 22C6.477 22 2 17.523 2 12S6.477 2 12 2s10 4.477 10 10-4.477 10-10 10zm0-2a8 8 0 1 0 0-16 8 8 0 0 0 0 16zm-1-5h2v2h-2v-2zm2-1.645V14h-2v-1.5a1 1 0 0 1 1-1 1.5 1.5 0 1 0-1.471-1.794l-1.962-.393A3.501 3.501 0 1 1 13 13.355z"></path></svg></span>`);
            $(".copy_hidden").after(`<span id="arg-error" style="display:none">如果出现参数错误是正常现象, 建议多试几次</span>`)
            let version=parseInt($(".rank__item:contains('修改') a").text());
            $("#version-choice").append(`<option value="-1">选择版本</option>`)
            for(let i=version;i>0;i--)
            {
                $("#version-choice").append(`<option value="${i}">第${i}版本</option>`)
            }
            $("#version-choice").change(function(){
                let t=$("#version-choice option:selected");
                toolkit.choice=parseInt(t.val());
            });
            $(".copy_hidden").click((e) => {
                e.preventDefault();
                e.stopPropagation();
                if (GM_getValue("warning") !== 0) {
                    let k = confirm(`\u672c\u94fe\u63a5\u7cfb\u672c\u4eba\u65e0\u610f\u95f4\u6293\u5305\u53d1\u73b0,\u5e94\u8be5\u662f\u7ed9\u8001\u5e08\u770b\u7684\u94fe\u63a5,\u5207\u52ff\u5927\u9762\u79ef\u4f20\u64ad,\u53ef\u80fd\u4f1a\u5bfc\u81f4\u94fe\u63a5\u88ab\u548c\u8c10,\u70b9\u51fb\u786e\u8ba4\u6253\u5f00\u7a97\u53e3`);
                    if (k) {
                        GM_setValue("warning", 0);
                    }
                    else {
                        return 0;
                    }
                }
                if(toolkit.choice===-1)
                {
                    alert(`\u8bf7\u9009\u62e9\u4f60\u8981\u5bf9\u6bd4\u7684\u7248\u672c\u53f7`);
                }
                else
                {
                    let link = `http://www.pigai.org/?c=sort&a=copy5&eid1=${unsafeWindow.eId}&v1=${toolkit.choice+1}&rid=${toolkit.getRid(window.location.href.indexOf("rid=") + 4)}&qz=1`;
                    GM_openInTab(link, {active: true});
                    $("#arg-error").show();
                }
            });
            return 0;
        }
        $("body").after(`<div class='cover'></div>`);
        $("[class='label']").eq(2).after(`&nbsp&nbsp<button id="bri_up" style="height:25px">亮度↑</button>&nbsp&nbsp<button id="bri_down" style="height:25px">亮度↓</button>&nbsp&nbsp<button class="day-mood" id="mood-switch"><svg class="day-svg" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="15" height="15" style="padding-top: 1px;"><path fill="none" d="M0 0h24v24H0z"/><path d="M9.973 18H11v-5h2v5h1.027c.132-1.202.745-2.194 1.74-3.277.113-.122.832-.867.917-.973a6 6 0 1 0-9.37-.002c.086.107.807.853.918.974.996 1.084 1.609 2.076 1.741 3.278zM10 20v1h4v-1h-4zm-4.246-5a8 8 0 1 1 12.49.002C17.624 15.774 16 17 16 18.5V21a2 2 0 0 1-2 2h-4a2 2 0 0 1-2-2v-2.5C8 17 6.375 15.774 5.754 15z" fill="#000"/></svg><svg class="night-svg" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="15" height="15" style="padding-top: 1px;"><path fill="none" d="M0 0h24v24H0z"/><path d="M11 18H7.941c-.297-1.273-1.637-2.314-2.187-3a8 8 0 1 1 12.49.002c-.55.685-1.888 1.726-2.185 2.998H13v-5h-2v5zm5 2v1a2 2 0 0 1-2 2h-4a2 2 0 0 1-2-2v-1h8z" fill="#000"/></svg><span class="mood-inner-text">夜间</span></button>`);
        $(".day-svg").hide();
        $("#bri_up").click(() => {
            if (toolkit.brightUtility.brightness - 0.05 >= 0) {
                toolkit.brightUtility.brightadjust(toolkit.brightUtility.brightness -= 0.05);
                toolkit.brightUtility.brightness = toolkit.my_tofixed(toolkit.brightUtility.brightness, 2, 1);
                unsafeWindow.msg(`当前亮度:${toolkit.my_tofixed(1.00 - toolkit.brightUtility.brightness, 2)}`);
            }
        });
        $("#bri_down").click(() => {
            if (toolkit.brightUtility.brightness + 0.05 < 0.95) {
                toolkit.brightUtility.brightadjust(toolkit.brightUtility.brightness += 0.05);
                toolkit.brightUtility.brightness = toolkit.my_tofixed(toolkit.brightUtility.brightness, 2, 1);
                unsafeWindow.msg(`当前亮度:${toolkit.my_tofixed(1.00 - toolkit.brightUtility.brightness, 2)}`);
            }
        });
        $("#mood-switch").click(() => toolkit.brightUtility.brightBtnClickEvent());
        if (GM_getValue("night")) {
            toolkit.brightUtility.brightBtnClickEvent(0);
        }
        //开始枚举各类情况
        if ($(".tips2").length > 0) {
            var have_time_limit = $('.tips2').html(),
                pointer;
            if (have_time_limit.match("写作时间") === null) {
                pointer = 0;
            }
            else {
                pointer = 1;
            }
        }
        if ($("#title") && ($(".tips2").length <= 0)) {
            crack.kind();
        }
        if ($(".tips2").length > 0 && have_time_limit.match("粘贴") !== null) {
            crack.main(pointer);
        }
        else if (pointer === 1) {
            crack.timer_disable();
        }
        //进行字数统计
        $('#contents').on('input propertychange', function () {
            toolkit.len = toolkit.spss_countwords($(this).val());
            $('#init_wc').html(toolkit.len);
            if (toolkit.len === 0) {
                $('#spss').attr("disabled", true).css("color", "grey");
            }
            else {
                $('#spss').attr("disabled", false).css("color", "blue");
            }
        });
        //退出前自动点击存草稿按钮, 防止误关闭
        window.onbeforeunload = () => {
            $('#save_bt').click();
        };
        toolkit.add_link();
        $(".title__folder.shou__qi").unbind();
        $("#contents,#title").focus(function () {
            $(this).css("background-color", "#ffffff").css("color","black");
        });
        $("#contents,#title").blur(function () {
            $(this).css("background-color", "#f9f9f9").css("color","#909090");
        });
        //自动移动焦点
        $("#title").blur();
        if($('#confirm').length > 0)
        {
            $('#confirm').focus();
        }
        else
        {
            $('#contents').focus();
        }
        //加20ms延时, 释放资源给浏览器
        setTimeout(() => {
            $("#links").css("transform", "none");
        }, 2e1)
    },

    run: () => {
        //跳过一切iframe
        if (window.self !== window.top) {
            return 0;
        }
        (() => {
            $('footer').remove();
            $('#pigaiMobile').remove();
            let len = $(".flexbox.nav").children().length - 1;
            let len_1 = len - 3;
            $("ul li:eq(" + len + ")").remove();
            $("ul li:eq(" + len_1 + ")").remove();
            $(".title__folder.shou__qi").remove();
        })();
        try {
            toolkit.init();
        }
        catch (e) {
            let em = prompt("脚本初始化出现问题, 已停止. 您可以点击'确认'进行反馈, 也可以点击'取消'忽略警告. 错误信息:", e.message);
            if (em !== null) {
                let m=encodeURIComponent(window.location.href);
                GM_openInTab(`https://github.com/consideration-ak/pigaiwang-toolkit/issues/new?title=报错:${e.message} url:${m} (请把这一段标题剪切到内容中)`, {loadInBackground: true});
            }
            GM_setClipboard("$('#contents').unbind();", {type: 'text', mimetype: 'text/plain'});
            prompt("如果破解禁止粘贴失败, 请在控制台console中手动输入如下代码(f12打开控制台)(已复制到剪切板)", "$('#contents').unbind();");
        }
    },

    brightUtility: {
        brightness: 0,
        brightadjust: (brightness) => {
            $(".cover").css('outlineColor', 'rgba(0,0,0,' + brightness + ')');
        },

        brightBtnClickEvent: (par = 1) => {
            try {
                document.getElementById(`mood-switch`).className;
            }
            catch (e) {
                toolkit.simpleToast.toast("初始化亮度按钮出现问题");
                return -1;
            }

            if (document.getElementById(`mood-switch`).className.match('day-mood') !== null) {
                toolkit.brightUtility.brightadjust(toolkit.brightUtility.brightness = 0.3);
                if (par) {
                    unsafeWindow.msg(`当前亮度:${toolkit.my_tofixed(1.00 - toolkit.brightUtility.brightness, 2)}`);
                    toolkit.simpleToast.toast("设置已保存");
                    GM_setValue("night", true);
                }
                $(".night-svg").hide();
                $(".day-svg").show();
                $(`.mood-inner-text`).text(`日间`);
                $(`#mood-switch`).attr("class", "night-mood");
            }
            else {
                toolkit.brightUtility.brightadjust(toolkit.brightUtility.brightness = 0);
                if (par) {
                    unsafeWindow.msg(`当前亮度:${toolkit.my_tofixed(1.00 - toolkit.brightUtility.brightness, 2)}`);
                    toolkit.simpleToast.toast("设置已保存");
                    GM_setValue("night", false);
                }
                $(".night-svg").show();
                $(".day-svg").hide();
                $(`.mood-inner-text`).text(`夜间`);
                $(`#mood-switch`).attr("class", "day-mood");
            }
        }
    }
};

//此处不能用window.onload, 因为mall.pigai.org经常加载非常慢, 对脚本有严重负面影响
$(document).ready(() => {
    toolkit.run();
});
